<?phprequire_once('prefs.php');require_once('./simplepie/simplepie.inc');require_once('./simplepie/idn/idna_convert.class.php');require_once('functions.php');?><!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd"><html xmlns="http://www.w3.org/1999/xhtml"><head>	<meta http-equiv="Content-Type" content="text/html; charset=utf-8" />	<title><?php echo $title; ?> </title>	<link  rel="stylesheet" href="style.css" type="text/css" /></head><body><div id="wrapper"><h1><?php echo $title ?></h1><?phpforeach($items as $item) { // start loop// reference to the parent $feed object for this particular item.$feeditem = $item->get_feed();$feedlink = $feeditem->get_permalink();if ($feeditem->get_title()) {	$feedtitle = $feeditem->get_title();	} else { // use url as link text if feed doesnt have a title	$feedtitle = $feedlink;}$itemlink = $item->get_permalink();$itemcontent = $item->get_content();if ($item->get_title()) {	$itemtitle = $item->get_title();	} else { // if the item has no title, use the content	$itemtitle = $itemcontent;}$itemdate = $item->get_date('j F Y'); // php date formatecho '<div class="item">';echo '<div class="item_header">';echo '<div class="item_date">'.$itemdate.'</div>';echo '<h2><a href="'.$itemlink.'">'.title_shorten ($itemtitle, 80).'</a></h2>';echo '</div>';echo '<div class="item_body">';if ($show_image == 'YES') {echo item_image($itemcontent) ;}echo shorten ($itemcontent, 250); // excerpt lengthecho '</div>'; //end of bodyecho '<div class="item_meta"><a href="'.$itemlink.'">Read full post</a> at <a href="'. $feedlink.'">'.$feedtitle.'</a></div>';echo '</div>'; // end of item} // end of loop?><div id="footer"><p>Powered by <a href="http://simplepie.org">SimplePie</a></p></div> <!-- footer --></div> <!-- wrapper --></body></html>